    <body>
        <!-- Header -->
        <header>
            <div class="navbar">
                   <!-- Logo -->
                   <a [routerLink]='["/home"]' class="logo">Building Your <span>Future</span>.
                    </a>
                <!-- Nav List -->
                <ul class="nav_links">
                    <li><a [routerLink]='["/courses"]'>Courses</a></li>
                    <li><a [routerLink]='["/tutorial"]'>Tutorial</a></li>
                    <li><a [routerLink]='["/about"]'>About</a></li>
                    <li><a [routerLink]='["/teachers"]'>Contact Us</a></li>
                </ul>
                <ul class="nav_login">
                  <li><a [routerLink]='["/login"]'>Login</a></li>
                  <li><a>Register</a></li>
                </ul>
            </div>
        </header>
        <!-- Home Section Start -->
        <section class="login_container" id="login">
            <div class="login_title">
                <h1>Building <br>Your <span>Future</span>.</h1>
            </div>
            <!-- Home Image -->
            <div class="login_img">
                <img src="assets/img/studenti.jpg">
            </div>


            <div class="login_section">
                <div class="form-container">
                    <p class="title">Register</p>
                    <form class="form">
                        <div class="input-group">
                            <label for="username">Nome completo</label>
                            <input #username="ngModel" [(ngModel)]="data.username" type="text" name="username" id="username" placeholder="">
                        </div>
                        <div class="input-group">
                            <label for="email">E-mail</label>
                            <input #email="ngModel" [(ngModel)]="data.email" type="text" name="email" id="email" placeholder="">
                        </div>
                        <div class="input-group">
                            <label for="password">Password</label>
                            <input #password="ngModel" [(ngModel)]="data.password" type="password" name="psw" id="psw" placeholder="">
                        </div>

                        <div class="input-group">
                            <label for="password">Ripeti Password</label>
                            <input type="password" name="psw" id="ripetipassword" placeholder="">
                        </div>

                        <input #isTeacher="ngModel" [(ngModel)]="data.isTeacher" type="radio" name="opzione" id="opzione1" value=false>
                        <label for="opzione1">Studente</label>

                        <input #isTeacher="ngModel" [(ngModel)]="data.isTeacher" type="radio" name="opzione" id="opzione2" value=true>
                        <label for="opzione2">Docente</label>

                        <button class="sign" (click)="register()">REGISTRATI</button>
                    </form>
                </div>
            </div>
        </section>
        <!-- ==================== FINE PARTE 1 ====================-->
