<div *ngIf="isTeacher">
  <div class="login_section">
    <div class="form-container">
      <!-- ... -->

      <div class="carousel">
        <!-- Singola Card del carosello -->
        <div class="card_carousel" *ngFor="let card of cards; let i = index">
          <div class="card">
            <ng-container *ngIf="!card.isEditing; else editingTemplate">
              <iframe [src]="getVideoUrl(card.link)" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
              <div class="testo_card">
                <h2>{{ card.titolo }}</h2>
                <h3>{{ card.materia }}</h3>
                <p>{{ card.descrizione }}</p>
              </div>
              <button class="sign2" (click)="modificaMateriale(i)">Modifica materiale</button>
              <button class="sign2" (click)="eliminaMateriale(i)">Elimina materiale</button>
            </ng-container>
